pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract ArtAlleyMarketplace is ReentrancyGuard {
    //item structure of makrketplace
    struct s_Marketpalceitem {
        address collectionAddress;
        uint256 tokenId;
        uint256 Price;
        bool approval;
    }
    //@dev here owner of NFT is the address parameter
    mapping(address => mapping(uint => s_Marketpalceitem))
        private _s_marketplaceItem;
    //@mapping from owner address to the total number of items related to the owner on marketplace
    mapping(address => uint[]) userTokenID;
    // array of all creators in markeplace
    address[] allCreators;

    //@dev: event will occur when item will be created on the marketplace
    event itemCreation(
        address indexed owner,
        address indexed collectionAddress,
        uint256 tokenId,
        uint256 Price,
        bool aproval
    );
    //@dev event occurs when new item is listed to be sold on marketplace
    event NewListingCreated(
        address indexed owner,
        address indexed collectionAddress,
        uint256 tokenId,
        uint256 listingPrice
    );
    //@dev event occurs when item is sold
    event listingsold(
        address indexed owner,
        address indexed collectionAddress,
        uint256 tokenId,
        uint256 Price
    );

    // @dev: this function is will initialise a new item on Marketplace
    function makeItem(
        address collectionAddress,
        uint256 tokenId,
        uint256 Price,
        bool approval
    ) public {
        require(Price > 0, "Price must be greater than zero");
        // New item is maped to the address of the owner
        _s_marketplaceItem[msg.sender][tokenId] = s_Marketpalceitem(
            collectionAddress,
            tokenId,
            Price,
            approval
        );
        // linking the users to  their tokenIds on the marketplace
        userTokenID[msg.sender].push(tokenId);
        allCreators.push(msg.sender);

        emit itemCreation(
            msg.sender,
            collectionAddress,
            tokenId,
            Price,
            approval
        );
    }

    function createListing(
        address collectionAddress,
        uint256 tokenId,
        uint256 listingPrice
    ) public {
        address owner = IERC721(collectionAddress).ownerOf(tokenId);

        s_Marketpalceitem memory item = _s_marketplaceItem[msg.sender][tokenId];
        require(
            owner != address(0),
            "NFT doesn't exits in the Collection or on Blockchain"
        );
        require(owner == msg.sender, "User is not the owner of the tokken");
        //Todo:rewrite the if condition
        require(
            IERC721(collectionAddress).isApprovedForAll(
                msg.sender,
                address(this)
            ),
            "Marketplace is not approved to list items"
        );
        if (item.collectionAddress == address(0)) {
            //aprove marketplace contract to buy and sell nfts

            // IERC721(collectionAddress).setApprovalForAll(address(this),true);
            //Create new item on marketplace
            makeItem(collectionAddress, tokenId, listingPrice, true);
        } else {
            _s_marketplaceItem[msg.sender][tokenId].approval = true;
        }
        emit NewListingCreated(
            msg.sender,
            collectionAddress,
            tokenId,
            listingPrice
        );
    }

    //buy available listings on the marketplace
    function buylisting(
        address collectionAddress,
        uint256 tokenId
    ) public payable {
        address owner = IERC721(collectionAddress).ownerOf(tokenId);
        s_Marketpalceitem memory item = _s_marketplaceItem[owner][tokenId];
        require(
            owner != address(0),
            "NFT doesn't exits in the Collection or on Blockchain"
        );
        require(
            item.collectionAddress != address(0),
            "Item is not listed on Marketplace"
        );
        require(
            item.approval == true,
            "Marketplace is not allowed to sell this tokken first createlisting on marketplace"
        );

        require(
            msg.value >= item.Price,
            "Amount send is less then the Price of the item."
        );

        //transfer tokken to msg.sender
        IERC721(collectionAddress).transferFrom(owner, msg.sender, tokenId);
        //transfer value to the owner of NFT
        payable(owner).transfer(msg.value);
        _s_marketplaceItem[msg.sender][tokenId].approval = false;
        emit listingsold(msg.sender, collectionAddress, tokenId, msg.value);
    }
}
